function observeSections(){const e=document.querySelectorAll("section"),t=document.querySelectorAll('a[href^="#"]'),c={root:null,rootMargin:"0px",threshold:.5},o=new IntersectionObserver(e=>{e.forEach(e=>{if(e.isIntersecting){t.forEach(e=>e.classList.remove("active"));const c=e.target.getAttribute("id"),o=document.querySelector(`a[href="#${c}"]`);o&&o.classList.add("active")}})},c);e.forEach(e=>o.observe(e))}document.getElementById("main-menu-button").addEventListener("click",function(){document.querySelector("header").classList.toggle("menu-active"),document.querySelector("header").classList.add("active"),document.querySelector("header").classList.contains("menu-active")?document.querySelector("body").classList.add("menu-active"):document.querySelector("body").classList.remove("menu-active")}),document.querySelectorAll('a[href^="#"]').forEach(e=>{e.addEventListener("click",function(e){e.preventDefault(),document.getElementById("main-menu-button").click(),document.querySelector(this.getAttribute("href")).scrollIntoView({behavior:"smooth"})})}),observeSections();